import {Box, Button, makeStyles, TextField} from "@material-ui/core"
import {EnterLottery} from "../hooks/hookMain"

const useStyles = makeStyles(() => ({
    columnFlex: {
        marginTop: "80px",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "green"
    },
    rowFlex:{
        display: "flex",
        flexDirection: "row",
        justifyContent: "center",
        padding: "5px",
    },
    button: {
        backgroundColor: "white"
    },
    input: {
        width: 60,
        height: 56,
        marginLeft: "5px",
        marginRight: "5px",
        textAlign: 'right',
        backgroundColor: 'red',
        border: "1px solid black"
    },
    wrapper: {
        width: "500px",
        backgroundColor: 'orange'
    },
    box: {
        display: "flex",
        flexDirection: "column"
    }
}))


export const Main = () => {

    const classes = useStyles()

    const hook = EnterLottery()

    let combination : Array<Number> = [-1,-1,-1,-1,-1,-1]

    const handleButton = () => {
        hook._enterLottery()
    }

    //TODO: TIMER JEL STARTOVAN ILI SE RACUNA WINNER
    //TODO: PLAY NE MOZE DA SE KLIKNE AKO NIJE VALIDAN INPUT U POLJA SA BROJEVIMA
    //TODO: VALIDAN INPUT BROJ IZMEDJU 1-48 I SVI RAZLICITI
    //TODO: POLJE ZACRVENI AKO NIJE VALIDAN INPUT
    return(
        <div className={classes.wrapper}>
            <Box className={classes.box}>
                <Box component="form" className={classes.rowFlex}>
                    <TextField className={classes.input} onChange={(v) => combination[0] = Number(v.target.value)} variant="outlined"/>
                    <TextField className={classes.input} onChange={(v) => combination[1] = Number(v.target.value)} variant="outlined"/>
                    <TextField className={classes.input} onChange={(v) => combination[2] = Number(v.target.value)} variant="outlined"/>
                    <TextField className={classes.input} onChange={(v) => combination[3] = Number(v.target.value)} variant="outlined"/>
                    <TextField className={classes.input} onChange={(v) => combination[4] = Number(v.target.value)} variant="outlined"/>
                    <TextField className={classes.input} onChange={(v) => combination[5] = Number(v.target.value)} variant="outlined"/>
                </Box>
                <Box className={classes.rowFlex}>
                    BLA
                </Box>
                <Box className={classes.rowFlex}>
                    <Button variant="outlined" size="large" onClick={handleButton} className={classes.button}>
                        Play
                    </Button>
                </Box>
            </Box>
        </div>
    )
}
